{{#scopeMap}}
flowVars.scopeMap.put("{{ key }}","{{ value }}");
{{/scopeMap}}
import json # dig the scope list out of the JSON map: r = flowVars['_agwTokenContext'] map = json.loads(r) scope = map['scope'] if 'scope' in map else '' flowVars['scope'] = scope k = 'X-AGW-scope' # apparently can't pass an immutable string as the key message.setOutboundProperty('X-AGW-scope',flowVars['scope']) # look for configured required scopes for this method and resource: scopeMap = flowVars['scopeMap'] lp = 'http.listener.path' listenerPath = message.getInboundProperty(lp) m = 'http.method' method = message.getInboundProperty(m).lower() rp = 'http.request.path' requestPath = message.getInboundProperty(rp) # strip the listener path out of the URL # http.listener.path=/v1/api/* # http.request.path=/v1/api/things # TODO: deal with a pattern match when URI-params are involved. print("requestPath: %s listenerPath: %s"%(requestPath,listenerPath)) if requestPath.find(listenerPath[:-2]) == 0: resource = requestPath[len(listenerPath)-2:] print("resource is %s method is %s"%(resource,method)) scopeMapKey = method+':'+resource.replace('/','&#x2F;') print("scopeMapkey is %s"%scopeMapKey) print(scopeMap) if scopeMapKey in scopeMap: requiredScopes = scopeMap[scopeMapKey] print("Required Scopes: %s. Granted Scopes: %s. Now intersect them!"%(requiredScopes,scope)) else: requiredScopes = '' else: print("request path doesn't match listener path") requiredScopes = '' flowVars['requiredScopes'] = requiredScopes flowVars['pyStatus'] = 200 if requiredScopes: if not set(requiredScopes.split(' ')).issubset(set(scope.split(' '))): print("Required Scopes are missing.") flowVars['pyStatus'] = 403 result=payload
